//Data Indexing
var jLen = 0
var kLen = 0
var aLen = 0

var journalToKeywordLen = {}

var jID = 1

var id_dict = {}
var jnum = 1

function thisBetterWork() {
  var branches = []; ////////
  // var b = branchSeed(seed1); ////////


  for (var journal in dataDict) {
    id_dict[jID.toString()] = journal
    var b = branchJournal(seed1, jID.toString())

    jLen += 1
    jnum += 1

    var kID = 1

    for (var kw in dataDict[journal]) {
      id_dict[jID.toString() + '-' + kID.toString()] = kw
      b = branchKeywordToJournal(b, jID.toString(), kID.toString());

      kLen += 1
      jnum += 1

      var aID= 1

      for (var article in dataDict[journal][kw]) {
        id_dict[jID.toString() + '-' + kID.toString() + '-' + aID.toString()] = dataDict[journal][kw][article]['title']
        aLen += 1
        jnum += 1

        aID +=1
      }
      kID += 1
    }
    journalToKeywordLen[jID.toString()] = kID
    jID += 1
  }

  branches.reverse();
  create();
}

var itemList = []

function createItemList() {
  for (var item in id_dict) {
    var newItem = item.split('-')
    itemList.push(newItem)
  }
}
createItemList();
//Setting initial variables

var branches = [];
var seed1 = {i: 0, x: 1000, y: 1200, a: 0, l: 400, d: 0, text: 'Computer Software', width: 40}; // a = angle, l = length, d = depth

var da = 2.1; // Angle delta
var dl = 0.85; // Length delta (factor)
var ar = 0; // Randomness
var maxDepth = 3;

function getAngleDelta(i, size) {
  var fraction = 6.28 / size
  return i * fraction
}

// function branchSeed(b) {
//   branches.push(b);
// }


function branchJournal(b, jIndex) {
  branches.push(b);
  var end = endPt(b), newB;
  var index_int = parseInt(jIndex);

  // eventually add an if statement here that evaluates the value of jIndex, and if above 3 extends the length
  return {
    i: branches.length,
    x: end.x,
    y: end.y,
    a: getAngleDelta(index_int, jLen),
    l: b.l *0.75,
    d: 1,
    text: id_dict[jIndex],
    parent: b.i,
    width: b.width*0.7,
  };
}

function branchKeywordToJournal(b, jIndex, aIndex) {
  branches.push(b);
  var end = endPt(b), newB;
  var jIndexInt = parseInt(jIndex);
  var aIndexInt = parseInt(aIndex);
  var index_str = jIndex + aIndex;
  var totalKeywords = journalToKeywordLen[jIndex];

  return {
    i: branches.length,
    x: end.x,
    y: end.y,
    a: getAngleDelta(aIndexInt, totalKeywords),
    l: b.l *0.75,
    d: 1,
    text: id_dict[index_str],
    parent: b.i,
    width: b.width*0.7,
  };

}

