function regenerate(initialise) {
  branches = [];
  branch(seed1);
  branches.reverse();
  createCircle();
  initialise ? create() : update();
}

function createCircle() {
  d3.select('svg')
    // .attr('width', (Object.keys(id_dict).length)*60)
    // .attr('height', (Object.keys(id_dict).length) * 45)
    .call(d3.behavior.zoom()
      .scaleExtent([0, 10])
      .on("zoom", function () {
        d3.select('g').attr("transform", "translate(" + d3.event.translate + ")" + " scale(" + d3.event.scale + ")")
    }))
    .append("g")
    .selectAll('circle') //  cx="0" cy="0" r="0"
    .data(branches)
    .enter()
    .insert('circle',":first-child")
    .on("mouseover", function(d) {

      d3.select(this).append("svg:title")
          .text(d.text)


      var depth = d.d;
      for (var i = 0; i <= depth; i++) {
        d3.select('#id-'+parseInt(d.i))
          .transition()
          .duration(30)
          .style('stroke',  "rgba(240, 30, 30, 1)")
          .transition()
          .duration(300)
          .style('stroke-width', function(d) {
              return d.width;
              // var t = parseInt( maxDepth + 30 - d.d *3.5);
              // return  t + 'px';
          });
        d = branches[d.parent];
      }
    })
    .on("mouseout", function(d) {
      var depth = d.d;
      for (var i = 0; i <= depth; i++) {
        d3.select( '#id-' + parseInt(d.i) )
          .transition()
          .duration(100)
          .style('stroke', color(d.d))
          // .duration(700)
          // .style('stroke-width', function(d) {
          //     var t = parseInt(maxDepth + 30 - d.d*4.5);
          //     return  t + 'px';
          // })
        d = branches[d.parent];
      }
    })
    .attr('cx', x2)
    .attr('cy', y2)
    .attr('r', 80);
}

function endPt(b) {
  // Return endpoint of branch
  var x = b.x + (b.l * Math.sin( b.a ));
  var y = b.y - (b.l * Math.cos( b.a ));
  return {x: x, y: y};
}


// D3 functions
var color = d3.scale.linear()
    .domain([0, maxDepth*0.25, maxDepth*0.5, maxDepth*0.75, maxDepth])
    // .range(["rgb(220, 180, 100)", "rgb(210, 170, 75)", "rgb(190, 140, 60)", "rgb(150, 110, 35)", "rgb(100, 75, 5)"]);
    // .range(["rgb(195, 155, 35)", "rgb(210, 175, 75)", "rgb(225, 195, 115)", "rgb(240, 215, 155)", "rgb(255, 235, 205)"]);
    // .range(["lightgrey", "crimson", "black", "crimson", "lightgrey"]);
    // .range(["rgba(20, 20, 20, 0.9)", "rgba(20, 20, 20, 0.92)", "rgba(20, 20, 20, 0.95)", "rgba(100, 20, 20, 0.98)", "rgba(220, 20, 20, 0.99)"]);
    .range(["rgba(200, 200, 200, 0.9)", "rgba(200, 200, 200, 0.9)", "rgba(200, 200, 200, 0.9)", "rgba(200, 140, 140, 0.9)", "rgba(240, 100, 100, 1)"]);

function x1(d) {return d.x;}
function y1(d) {return d.y;}
function x2(d) {return endPt(d).x;}
function y2(d) {return endPt(d).y;}
// function addText(d) { return d.text = id_dict[d.i]}


function create() {
  d3.select('g')
    .selectAll('line')
    .data(branches)
    .enter()
    .insert('line',":first-child")
    .on("mouseover", function(d) {

      var pathList = [];
      var bigD = d;
      var depth = bigD.d;
      for (var i = 0; i <= depth; i++) {
        var focus = d3.select('#id-'+parseInt(bigD.i));
        pathList.push(bigD.text);
        bigD = branches[bigD.parent];
      }
      var path = pathList.join(' > ')

      d3.select(this).append("svg:title")
          .text(path)


      var depth = d.d;
      for (var i = 0; i <= depth; i++) {
        d3.select('#id-'+parseInt(d.i))
          .transition()
          .duration(30)
          .style('stroke',  "rgba(240, 30, 30, 1)")
          // .transition()
          // .duration(300)
          // .style('stroke-width', function(d) {
          //     return d.width;
          //     // var t = parseInt( maxDepth + 30 - d.d *3.5);
          //     // return  t + 'px';
          // });
        d = branches[d.parent];
      }
    })
    .on("mouseout", function(d) {
      var depth = d.d;
      for (var i = 0; i <= depth; i++) {
        d3.select( '#id-' + parseInt(d.i) )
          .transition()
          .duration(100)
          .style('stroke', color(d.d))
          // .duration(700)
          // .style('stroke-width', function(d) {
          //     var t = parseInt(maxDepth + 30 - d.d*4.5);
          //     return  t + 'px';
          // })
        d = branches[d.parent];
      }
    })
    .attr('x1', x1)
    .attr('y1', y1)
    .attr('x2', x2)
    .attr('y2', y2)
    .transition()
    .duration(1500)
    .style('stroke-width', function(d) {
        return d.width;
        // var t = parseInt(maxDepth + 30 - d.d*4.5);
        // return  t + 'px';
    })
    .style('stroke', function(d) {
        return color(d.d);
    })
    .attr('id', function(d) {
      return 'id-'+d.i;
    });
}

function update() {
  d3.select('svg')
    .selectAll('line')
    .data(branches)
    .attr('x1', x1)
    .attr('y1', y1)
    .attr('x2', x2)
    .attr('y2', y2);
}

d3.selectAll('.regenerate')
  .on('click', regenerate);

regenerate(true);


















